function Button(){

    const handleclick = (name) => {
        let count=0;
        if(count<3){
            count++;
            console.log(`${name} clicked me ${count} times`);
        }
        else{
            console.log(`${name} Stop clicking me :(`);
        }
    }

    return(<button onClick={ ()=> handleclick}>Click Me </button>)
}

export default Button
