import React from 'react';

const TransactionHistory = ({ transactions, deleteTransaction, lastTransactionType }) => {
  return (
    <div className="bg-white p-4 rounded shadow-md">
      <h2 className="text-xl mb-4">Transaction History</h2>
      {lastTransactionType && (
        <p className="text-lg font-semibold mb-2">
          Last transaction: <span className={lastTransactionType === 'Income' ? 'text-green-500' : 'text-red-500'}>{lastTransactionType}</span>
        </p>
      )}
      <ul>
        {transactions.map((transaction) => (
          <li
            key={transaction.id}
            className={`flex justify-between items-center py-2 border-b ${transaction.amount > 0 ? 'text-green-500' : 'text-red-500'}`}
          >
            <div>
              <p className="text-lg font-bold">
                {transaction.amount > 0 ? '+' : '-'}${Math.abs(transaction.amount)}
              </p>
              <p>{transaction.description}</p>
            </div>
            <button onClick={() => deleteTransaction(transaction.id)} className="text-red-500">
              üóëÔ∏è
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionHistory;
