import React, { useState } from 'react';
import TransactionHistory from './TransactionHistory';

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [lastTransactionType, setLastTransactionType] = useState('');

  const addTransaction = (amount, description) => {
    const newTransaction = {
      id: transactions.length + 1,
      amount,
      description,
    };
    setTransactions([...transactions, newTransaction]);

    // Determine if the transaction is income or expense
    setLastTransactionType(amount > 0 ? 'Income' : 'Expense');
  };

  const deleteTransaction = (id) => {
    setTransactions(transactions.filter(transaction => transaction.id !== id));
  };

  return (
    <div>
      <h1>Transaction Tracker</h1>
      <button onClick={() => addTransaction(100, 'Salary')}>Add Income</button>
      <button onClick={() => addTransaction(-50, 'Groceries')}>Add Expense</button>
      <TransactionHistory transactions={transactions} deleteTransaction={deleteTransaction} lastTransactionType={lastTransactionType} />
    </div>
  );
};

export default App;
